const chai = require('chai');
chai.use(require('dirty-chai'));
const expect = chai.expect;

const marco = require('../../index');

const sampleData = require('../../resources/samples/sample-marco-workflow-dispatch-create.json');

describe('Sample marco dispatch create workflow', function() {
  it('should validate against marco schema', function() {
    const isValid = marco.v1.validate(
      'https://app.temando.com/schema/generic/marco-definition+v1#',
      sampleData
    );

    if (!isValid) console.log(marco.v1.errors);

    expect(isValid).to.be.ok();
  });
});
